import { StyleSheet } from "react-native";

const styles = StyleSheet.create({
  container: {
    width: "100%",
    flexDirection: "row",
    padding: 12,
    borderBottomWidth: 0.5,
    borderColor: "grey",
  },
});

export default styles;
