import React from 'react'
import { View, Text, Image } from 'react-native'


export type ProfilePictureProps = {
    image: string,
    size?: number
}


const ProfilePicture = ({image,size = 50}: ProfilePictureProps) => (
            <Image 
            source={{ uri:image}}
            style={{
                width:size,
                height: size,
                borderRadius: size
            }}
            />
    )


export default ProfilePicture
