import { StyleSheet } from "react-native";

const styles = StyleSheet.create({
  mainContainer: {
    flex: 1,
  },
  tweetHeaderContainer: {
    flexDirection: "row",
    justifyContent: "space-between",
  },
  tweetHeaderNames: {
    flexDirection: "row",
  },
  name: {
    marginHorizontal: 5,
    fontWeight: "bold",
  },
  username: {
    marginHorizontal: 5,
    color: "grey",
  },
  craetedAt: {
    marginHorizontal: 5,
    color: "grey",
  },
  moreIcon: {
    alignSelf: "flex-end",
  },
});

export default styles;
